export default function Tasks() {
  const assignedTasks = [
    { title: "Task 1", desc: "Complete it till 2", status: "pending" },
  ];

  const createdTasks = [
    { title: "Task 1", desc: "Complete it till 2", status: "pending" },
  ];

  const TaskCard = ({ task }) => (
    <div className="p-4 border rounded-lg shadow bg-white">
      <h3 className="font-semibold">{task.title}</h3>
      <p className="text-gray-600 text-sm">{task.desc}</p>
      <p className="text-xs mt-1">
        <span className="font-medium">Status:</span>{" "}
        <span className="text-red-500">{task.status}</span>
      </p>
    </div>
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Tasks</h1>

      {/* Assigned Tasks */}
      <div className="mb-6">
        <h2 className="text-lg font-semibold mb-3">Tasks Assigned to You</h2>
        <div className="space-y-3">
          {assignedTasks.map((t, i) => (
            <TaskCard key={i} task={t} />
          ))}
        </div>
      </div>

      {/* Created Tasks */}
      <div>
        <h2 className="text-lg font-semibold mb-3">Tasks Created by You</h2>
        <div className="space-y-3">
          {createdTasks.map((t, i) => (
            <TaskCard key={i} task={t} />
          ))}
        </div>
      </div>
    </div>
  );
}
