import React, { useState } from "react";

export default function Tasks({ selectedTeam, onAddTask }) {
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  // If selectedTeam is undefined, don't render the component
  if (!selectedTeam) return null;

  const handleAdd = () => {
    if (!title || !desc) return;
    const task = { title, desc, status: "pending", teamId: selectedTeam.id };
    onAddTask(task);
    setTitle("");
    setDesc("");
  };

  const TaskCard = ({ task }) => (
    <div className="p-4 border rounded-lg shadow bg-white">
      <h3 className="font-semibold">{task.title}</h3>
      <p className="text-gray-600 text-sm">{task.desc}</p>
      <p className="text-xs mt-1">
        <span className="font-medium">Status:</span>{" "}
        <span className="text-red-500">{task.status}</span>
      </p>
    </div>
  );

  return (
    <div className="p-6 bg-gray-50 rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">
        Tasks for {selectedTeam.name} ({selectedTeam.domain || "No domain"})
      </h2>

      {/* Add Task */}
      <div className="mb-6 space-y-2">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <textarea
          placeholder="Task Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <button
          onClick={handleAdd}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:opacity-90"
        >
          Add Task
        </button>
      </div>

      {/* Task List */}
      <div className="space-y-3">
        {selectedTeam.tasks?.length === 0 ? (
          <p className="text-gray-500">No tasks assigned yet.</p>
        ) : (
          selectedTeam.tasks.map((t, i) => <TaskCard key={i} task={t} />)
        )}
      </div>
    </div>
  );
}
