import { useState } from "react";
import { Eye, EyeOff, Check } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";
import { useNavigate } from "react-router-dom";

// ‚úÖ Import images from src/assets
import logo from "../assets/UpToSkills_Logo.svg";
import aiImg from "../assets/login_psge_img.jpg";
import googleIcon from "../assets/google_icon.png";
import appleIcon from "../assets/apple_icon.png";

export default function SignupPage() {
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const payload = Object.fromEntries(formData.entries());

    const response = await fetch("/api/auth/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();

    if (response.ok) {
      alert("‚úÖ Signup successful!");
      localStorage.setItem("signupEmail", payload.email);
      localStorage.setItem("signupPassword", payload.password);
      navigate("/login");
    } else {
      alert(data.message || "Signup failed!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-10">
          <img src={logo} alt="Logo" className="h-12 mb-6" />
          <h1 className="text-5xl font-extrabold leading-tight">
            <span className="bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
              AI Learning
            </span>{" "}
            <span className="text-black">Platform</span>
          </h1>
          <p className="text-gray-600 mt-6 text-lg">
            Unlock the future of education with AI-powered courses
            <br />
            designed to accelerate your learning journey
          </p>
          <div className="mt-10 flex justify-center">
            <img
              src={aiImg}
              alt="AI Illustration"
              className="max-w-sm"
            />
          </div>
        </div>

        {/* Right Section */}
        <CardContent className="p-10 bg-white flex flex-col justify-center">
          <h2 className="text-xl font-bold mb-1">Join Us Today!</h2>
          <p className="text-gray-500 mb-6 text-sm">
            Create your account for an enhanced experience at your fingertips.
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            <Input
              type="email"
              name="email"
              placeholder="Enter your email here"
              required
              className="placeholder:text-gray-400"
            />
            <Input
              type="text"
              name="username"
              placeholder="Enter your username here"
              required
              className="placeholder:text-gray-400"
            />
            
            <div className="relative">
              <Input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Create a Password"
                required
                className="pr-10 placeholder:text-gray-400"
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-gray-500"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>

            {/* Password Hint */}
            <div className="flex items-center text-sm text-gray-600">
              <Check size={16} className="text-green-600 mr-2" />
              Minimum 8 characters required
            </div>

            <Button className="w-full rounded-xl bg-sky-500 hover:bg-sky-600 text-white p-3 text-lg">
              Sign Up
            </Button>
          </form>

          {/* Divider */}
          <div className="flex items-center my-6">
            <hr className="flex-grow border-gray-300" />
            <span className="px-3 text-gray-400 text-sm">Or</span>
            <hr className="flex-grow border-gray-300" />
          </div>

          {/* Social Logins */}
          <div className="space-y-3 mt-4">
            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src={googleIcon} alt="Google" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Sign in with Google</span>
            </button>

            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src={appleIcon} alt="Apple" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Sign in with Apple</span>
            </button>
          </div>

          {/* Switch Link */}
          <p className="text-center text-gray-600 mt-6 text-sm">
            Already have an account?{" "}
            <a href="/login" className="text-red-500 font-semibold">
              Log In
            </a>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}




