// src/components/KanbanBoard.jsx
import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const initialColumns = {
  "todo": {
    name: "To Do",
    items: []
  },
  "inprogress": {
    name: "In Progress",
    items: []
  },
  "done": {
    name: "Done",
    items: []
  }
};

export default function KanbanBoard({ tasks }) {
  const [columns, setColumns] = useState({
    ...initialColumns,
    todo: { ...initialColumns.todo, items: tasks || [] }
  });

  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;

    const { source, destination } = result;
    const sourceCol = columns[source.droppableId];
    const destCol = columns[destination.droppableId];
    const sourceItems = [...sourceCol.items];
    const destItems = [...destCol.items];

    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);

    setColumns({
      ...columns,
      [source.droppableId]: { ...sourceCol, items: sourceItems },
      [destination.droppableId]: { ...destCol, items: destItems },
    });
  };

  return (
    <div className="flex gap-4 overflow-x-auto p-4">
      <DragDropContext
        onDragEnd={result => onDragEnd(result, columns, setColumns)}
      >
        {Object.entries(columns).map(([colId, col]) => (
          <Droppable key={colId} droppableId={colId}>
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                className="bg-white rounded-xl p-4 w-80 flex-shrink-0 shadow-md"
              >
                <h2 className="text-lg font-bold mb-4">{col.name}</h2>
                {col.items.map((item, index) => (
                  <Draggable key={item._id} draggableId={item._id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="mb-3 p-3 bg-blue-50 rounded-lg shadow cursor-pointer"
                      >
                        <h3 className="font-semibold">{item.title}</h3>
                        <p className="text-sm text-gray-600">{item.desc}</p>
                        <p className="text-xs mt-1 text-gray-500">
                          Assigned: {item.assignedTo?.join(", ")}
                        </p>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
}
