import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext"; // From AuthContext
import axios from "axios";
import { LogOut } from "lucide-react"; // Icon for logout (used in Navbar if needed)

// Sub-component imports (create these files in src/components/ - see placeholders in previous responses)
import Sidebar from "./Sidebar";
import Navbar from "./Navbar";
import Dashboard from "./Dashboard";
import Teams from "./Teams";
import Tasks from "./Tasks";
import Notifications from "./Notifications";
import Calendar from "./Calendar";
import Profile from "./Profile";

export default function DashboardPage() {
  const [activeSection, setActiveSection] = useState("dashboard");
  const { user, logout, socket } = useAuth(); // Get user, logout, socket from context
  const navigate = useNavigate();

  // Axios interceptor to attach token to all requests
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    }

    // Handle 401 errors globally (auto-logout)
    const interceptor = axios.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          logout();
          navigate("/login");
        }
        return Promise.reject(error);
      }
    );

    return () => axios.interceptors.response.eject(interceptor);
  }, [logout, navigate]);

  // Socket setup: Join user room for realtime
  useEffect(() => {
    if (socket && user) {
      socket.emit("join", user.id); // Join user's room
      return () => {
        if (socket) socket.disconnect(); // Cleanup on unmount
      };
    }
  }, [socket, user]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const renderContent = () => {
    switch (activeSection) {
      case "dashboard":
        return <Dashboard user={user} />;
      case "teams":
        return <Teams user={user} setActiveSection={setActiveSection} />;
      case "tasks":
        return <Tasks user={user} />;
      case "notifications":
        return <Notifications user={user} socket={socket} />;
      case "calendar":
        return <Calendar user={user} />;
      case "profile":
        return <Profile user={user} />;
      default:
        return <Dashboard user={user} />;
    }
  };

  // Show loading if user is not yet loaded from context
  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-lg">Loading...</div>
      </div>
    ); // Fallback
  }

  return (
    <div className="min-h-screen flex flex-col bg-gray-100">
      {/* Navbar at the top (full width) - Pass logout handler */}
      <Navbar user={user} onLogout={handleLogout} />

      <div className="flex flex-1">
        {/* Sidebar on the left */}
        <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />

        {/* Main content area */}
        <div className="flex-1 flex flex-col">
          <main className="flex-1 p-8 overflow-y-auto">
            {renderContent()}
          </main>
        </div>
      </div>
    </div>
  );
}
