import React, { useEffect, useState } from "react";
import io from "socket.io-client";
import Tasks from "./Tasks";
import Teams from "./Teams";

const socket = io("http://localhost:5000");

export default function DashboardPage() {
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    socket.on("taskCreated", (task) => {
      setNotifications((prev) => [
        `New Task "${task.title}" assigned to ${task.assignedTo.join(", ")}`,
        ...prev,
      ]);
    });

    return () => socket.off("taskCreated");
  }, []);

  return (
    <div className="p-6 space-y-6">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Teams */}
        <div>
          <Teams ref={{}} />
          <button
            className="mt-4 bg-blue-600 text-white px-4 py-2 rounded"
            onClick={() => setSelectedTeam(null)}
          >
            Select Team
          </button>
        </div>

        {/* Tasks */}
        {selectedTeam && <Tasks selectedTeam={selectedTeam} />}
      </div>

      <div className="mt-6 bg-white p-4 rounded shadow">
        <h2 className="font-bold mb-2">Notifications</h2>
        <ul className="space-y-2 max-h-48 overflow-y-auto">
          {notifications.map((n, i) => (
            <li key={i} className="p-2 border rounded bg-gray-50 text-sm">
              {n}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
