import { useState } from "react";
import Sidebar from "./Sidebar";
import Teams from "./Teams";
import Tasks from "./Tasks";
import Notifications from "./Notifications";
import Calendar from "./Calendar";
import Profile from "./Profile";
import welcomeImg from "../assets/welcome.png";

export default function DashboardPage() {
  const [activeSection, setActiveSection] = useState("dashboard");

  const renderContent = () => {
    switch (activeSection) {
      case "teams":
        return <Teams />;
      case "tasks":
        return <Tasks />;
      case "notifications":
        return <Notifications />;
      case "calendar":
        return <Calendar />;
      case "profile":
        return <Profile />;
      case "dashboard":
      default:
        return (
          <div className="bg-white rounded-2xl shadow-lg p-8 flex flex-col items-center text-center relative overflow-hidden">
            <div className="absolute inset-0 bg-gradient-to-r from-blue-50 via-purple-50 to-pink-50 opacity-50 rounded-2xl"></div>
            <div className="relative z-10">
              <img
                src={welcomeImg}
                alt="Welcome"
                className="w-72 h-72 object-contain mx-auto mb-6 drop-shadow-lg"
              />
              <h2 className="text-3xl font-extrabold text-gray-800 mb-4">
                Welcome to the{" "}
                <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600">
                  AI-Powered Ideation & Project Management Platform
                </span>
              </h2>
              <p className="text-gray-600 max-w-3xl leading-relaxed text-lg">
                Unlock creativity and productivity with AI. Our platform empowers your team to{" "}
                <span className="font-semibold text-blue-600">brainstorm ideas</span>,{" "}
                <span className="font-semibold text-purple-600">collaborate seamlessly</span>, and{" "}
                <span className="font-semibold text-pink-600">manage projects efficiently</span>.
                Use the sidebar to explore <strong>Dashboard</strong>, <strong>Teams</strong>,{" "}
                <strong>Tasks</strong>, <strong>Notifications</strong>, <strong>Calendar</strong>, and{" "}
                <strong>Profile</strong>.
              </p>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen flex bg-gray-100">
      <Sidebar activeSection={activeSection} setActiveSection={setActiveSection} />
      <div className="flex-1 flex flex-col">
        <main className="flex-1 p-8 overflow-y-auto">{renderContent()}</main>
      </div>
    </div>
  );
}
