import { useState } from "react";
import { Eye, EyeOff, Check } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";
import { useNavigate } from "react-router-dom";

export default function SignupPage() {
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const payload = Object.fromEntries(formData.entries());

    const response = await fetch("/api/auth/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();

    if (response.ok) {
      alert("✅ Signup successful!");
      // Save email + password to localStorage
      localStorage.setItem("signupEmail", payload.email);
      localStorage.setItem("signupPassword", payload.password);
      // Redirect to login page
      navigate("/login");
    } else {
      alert(data.message || "Signup failed!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-10">
          <img src="/assets/UpToSkills_Logo.svg" alt="Logo" className="h-12 mb-6" />
          <h1 className="text-4xl font-extrabold">
            <span className="text-blue-500">AI Learning</span>{" "}
            <span className="text-black">Platform</span>
          </h1>
          <p className="text-gray-600 mt-4 text-lg">
            Unlock the future of education with AI-powered courses
            <br />
            designed to accelerate your learning journey
          </p>
          <div className="mt-8 flex justify-center">
            <img src="/assets/login_psge_img.jpg" alt="AI Illustration" className="max-w-sm" />
          </div>
        </div>

        {/* Right Section */}
        <CardContent className="p-10 bg-gray-50 flex flex-col justify-center">
          <h2 className="text-2xl font-bold mb-2">Join Us Today!</h2>
          <p className="text-gray-500 mb-6">
            Create your account for an enhanced experience at your fingertips.
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            <Input type="email" name="email" placeholder="Email Address" required />
            <Input type="text" name="username" placeholder="Choose a Username" required />
            <div className="relative">
              <Input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Create a Password"
                required
                className="pr-10"
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-gray-500"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>

            {/* Password Hint */}
            <div className="flex items-center text-sm text-gray-600">
              <Check size={16} className="text-green-600 mr-2" />
              Minimum 8 characters required
            </div>

            <Button className="w-full rounded-xl bg-blue-600 hover:bg-blue-700 text-white p-3">
              Sign Up
            </Button>
          </form>

          {/* Social Logins */}
          <div className="my-6 text-center text-gray-400 text-sm">Or</div>
          <div className="flex flex-col gap-3">
            <button className="flex items-center justify-center gap-3 border px-6 py-3 rounded-full hover:bg-gray-100 transition">
              <img src="/assets/google_icon.svg" alt="Google" className="h-5 w-5" />
              <span className="text-gray-700 font-medium">Sign up with Google</span>
            </button>
            <button className="flex items-center justify-center gap-3 border px-6 py-3 rounded-full hover:bg-gray-100 transition">
              <img src="/assets/apple_icon.svg" alt="Apple" className="h-5 w-5" />
              <span className="text-gray-700 font-medium">Sign up with Apple</span>
            </button>
          </div>

          {/* Switch Link */}
          <p className="text-center text-gray-600 mt-6">
            Already have an account?{" "}
            <a href="/login" className="text-blue-600 font-semibold">
              Log In
            </a>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
