// src/components/Dashboard.jsx
import React, { useState } from "react";
import { CheckCircle, Trash2, Copy } from "lucide-react";
import { useTeams } from "../context/TeamContext";

export default function Dashboard() {
  const { teams } = useTeams();
  const [activities, setActivities] = useState([]);
  const [newActivity, setNewActivity] = useState("");

  const addActivity = (e) => {
    e.preventDefault();
    if (!newActivity.trim()) return;
    setActivities([newActivity, ...activities]);
    setNewActivity("");
  };

  return (
    <div className="p-6 space-y-6">
      {/* Teams Section */}
      <section>
        <h2 className="text-lg font-semibold">Your Teams</h2>
        {teams.length === 0 ? (
          <p className="text-gray-500">No teams created yet.</p>
        ) : (
          <ul className="space-y-2">
            {teams.map((team) => (
              <li
                key={team.id}
                className="px-4 py-2 border rounded-lg bg-gray-50 flex justify-between items-center"
              >
                <span>
                  {team.name} - {team.domain || "No domain selected"} ({team.tasks.length} tasks)
                </span>
              </li>
            ))}
          </ul>
        )}
      </section>

      {/* Activities Section */}
      <section className="bg-white shadow rounded-xl p-4 space-y-3">
        <h2 className="text-lg font-semibold">ðŸ“Œ Team Activity</h2>
        <form className="flex gap-2" onSubmit={addActivity}>
          <input
            type="text"
            value={newActivity}
            onChange={(e) => setNewActivity(e.target.value)}
            className="flex-1 border rounded-lg px-2 py-1 text-sm"
            placeholder="Add an activity..."
          />
          <button className="bg-blue-600 text-white px-3 rounded-lg hover:bg-blue-700">
            + Add
          </button>
        </form>
        <ul className="space-y-2 max-h-40 overflow-y-auto">
          {activities.map((act, idx) => (
            <li key={idx} className="px-3 py-2 border rounded-lg text-sm bg-gray-50">
              {act}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
