import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { Mail, Lock } from "lucide-react";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);

  const { setAuthData } = useAuth();
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    setLoading(true);

    // ‚úÖ For testing, simulate a login delay then navigate
    setTimeout(() => {
      // Optional: set dummy auth data
      setAuthData("dummy-token", { email });
      
      // Redirect to dashboard
      navigate("/dashboard");
    }, 500); // half a second delay to mimic loading
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-br from-purple-600 via-pink-500 to-blue-500 px-4">
      <div className="absolute w-72 h-72 bg-pink-500/30 rounded-full blur-3xl top-12 left-12 animate-pulse"></div>
      <div className="absolute w-96 h-96 bg-purple-500/30 rounded-full blur-3xl bottom-12 right-12 animate-pulse"></div>

      <div className="relative z-10 bg-white/20 backdrop-blur-xl rounded-3xl shadow-2xl w-full max-w-md p-8 border border-white/30">
        <div className="mb-6 text-center">
          <h2 className="text-3xl font-extrabold text-white">Welcome Back</h2>
          <p className="text-white/80 text-sm mt-1">Log in to continue üöÄ</p>
        </div>

        <form className="space-y-5" onSubmit={handleLogin}>
          <div>
            <label className="block text-sm font-medium text-white mb-1">Email</label>
            <div className="relative">
              <Mail className="absolute left-3 top-3.5 text-purple-400 w-5 h-5" />
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full pl-10 pr-4 py-3 rounded-lg bg-white/90 text-gray-900 focus:ring-2 focus:ring-purple-500"
                required
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-white mb-1">Password</label>
            <div className="relative">
              <Lock className="absolute left-3 top-3.5 text-blue-400 w-5 h-5" />
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full pl-10 pr-12 py-3 rounded-lg bg-white/90 text-gray-900 focus:ring-2 focus:ring-blue-500"
                required
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-sm text-pink-600"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 text-white font-semibold py-3 rounded-lg shadow-lg transition-all ${
              loading
                ? "opacity-50 cursor-not-allowed"
                : "hover:from-blue-700 hover:via-purple-700 hover:to-pink-700"
            }`}
          >
            {loading ? "Logging In..." : "Log In"}
          </button>
        </form>

        <p className="text-center text-white/90 text-sm mt-6">
          Don‚Äôt have an account?{" "}
          <Link to="/signup" className="text-yellow-300 hover:underline">
            Sign up
          </Link>
        </p>
      </div>
    </div>
  );
}
