// src/components/DashboardPage.jsx
import React from "react";
import { useTeam } from "../context/TeamContext";
import { Bell } from "lucide-react";

export default function DashboardPage({ onScrollToTask }) {
  const { teams, tasks } = useTeam();
  const pendingTasks = tasks.filter((t) => t.status === "Pending").length;

  return (
    <div className="p-6 space-y-6">
      {/* Header with Notifications */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Dashboard</h1>
        <div className="relative">
          <Bell size={24} />
          {pendingTasks > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full px-2">
              {pendingTasks}
            </span>
          )}
        </div>
      </div>

      {/* Notifications */}
      <section>
        <h2 className="text-xl font-bold mb-2">Notifications</h2>
        {tasks.length === 0 ? (
          <p className="text-gray-500">No notifications yet.</p>
        ) : (
          <ul className="space-y-2">
            {tasks.map((task) => {
              const team = teams.find((t) => t.id === task.teamId);
              return (
                <li
                  key={task.id}
                  className={`border px-3 py-2 rounded bg-gray-50 cursor-pointer ${
                    task.status === "Completed"
                      ? "line-through text-green-600"
                      : ""
                  }`}
                  onClick={() => onScrollToTask?.(task.id)}
                >
                  Task "{task.title}" in team "{team?.name}" - {task.status}
                </li>
              );
            })}
          </ul>
        )}
      </section>
    </div>
  );
}
