import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

export default function Tasks({ selectedTeam }) {
  const { user, token } = useAuth();

  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [assigned, setAssigned] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchTasks = async () => {
    if (!selectedTeam) return;
    setLoading(true);

    const res = await axios.get("http://localhost:5000/api/tasks", {
      headers: { Authorization: `Bearer ${token}` },
    });

    setTasks(res.data.filter(t => t.teamId === selectedTeam._id));
    setLoading(false);
  };

  useEffect(() => { fetchTasks(); }, [selectedTeam]);

  const addTask = async () => {
    if (!title.trim()) return alert("Task title required!");

    const assignees = assigned.split(",").map(e => e.trim()).filter(Boolean);

    const res = await axios.post(
      "http://localhost:5000/api/tasks",
      { title, desc, teamId: selectedTeam._id, assignedTo: assignees },
      { headers: { Authorization: `Bearer ${token}` }}
    );

    setTasks(prev => [...prev, res.data]);
    setTitle(""); setDesc(""); setAssigned("");
  };

  return (
    <div className="p-6 bg-white rounded-xl shadow-md">

      <h2 className="font-bold text-xl mb-4">Team: {selectedTeam?.name}</h2>

      {/* Task Creation Only For Manager */}
      {user?.role === "manager" && (
        <div className="space-y-2 mb-5">
          <input className="border p-2 w-full" placeholder="Task title"
            value={title} onChange={(e)=>setTitle(e.target.value)} />
          <textarea className="border p-2 w-full" placeholder="Description"
            value={desc} onChange={(e)=>setDesc(e.target.value)} />
          <input className="border p-2 w-full"
            placeholder={`Assign: ${selectedTeam?.members.join(", ")}`}
            value={assigned} onChange={(e)=>setAssigned(e.target.value)} />

          <button onClick={addTask}
            className="bg-green-600 text-white px-6 py-2 rounded-md">
            ➕ Add Task
          </button>
        </div>
      )}

      {/* Show Task List */}
      {loading ? <p>Loading...</p> :
      tasks.length === 0 ? <p>No Tasks</p> :
      tasks.map((t) => (
        <div key={t._id} className="border p-3 mb-2 rounded-md">
          <b>{t.title}</b>
          <p className="text-gray-700 text-sm">{t.desc}</p>
          <p className="text-xs text-gray-600">Assigned → {t.assignedTo.join(", ")}</p>
        </div>
      ))}
    </div>
  );
}
