import { useState } from "react";
import { Eye, EyeOff, Check } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";
import { useNavigate } from "react-router-dom";

export default function SignupPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const payload = { name, email, password };
    console.log("üöÄ Sending signup data:", payload);

    try {
      const response = await fetch("http://localhost:5000/api/auth/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (response.ok) {
        alert("‚úÖ Signup successful!");
        navigate("/login", { state: { email, password } }); // prefill login
      } else {
        alert(data.error || "Signup failed!");
      }
    } catch (err) {
      console.error("Signup error:", err);
      alert("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-50 via-white to-purple-50 px-6">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        
        {/* Left Section */}
        <div className="flex flex-col items-center justify-center bg-white px-10 py-12 text-center">
          <img src="/assets/UpToSkills_Logo.svg" alt="Logo" className="h-14 mb-6" />
          <h1 className="text-4xl font-extrabold leading-snug">
            <span className="bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
              Join AI Learning
            </span>{" "}
            <span className="text-black">Today</span>
          </h1>
          <p className="text-gray-600 mt-4 text-lg leading-relaxed max-w-sm">
            Unlock the future of education with{" "}
            <span className="font-semibold text-blue-500">AI-powered courses</span> 
            designed to accelerate your learning journey.
          </p>
          <img
            src="/assets/login_psge_img.jpg"
            alt="AI Illustration"
            className="mt-10 w-64 rounded-xl shadow-lg"
          />
        </div>

        {/* Right Section (Signup Form) */}
        <CardContent className="flex items-center justify-center bg-white">
          <div className="w-full max-w-md bg-white p-6">
            <div className="text-center mb-6">
              <h2 className="text-3xl font-extrabold bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
                Create Account
              </h2>
              <p className="text-gray-500 text-sm">Start your AI learning journey today üöÄ</p>
            </div>

            <form className="space-y-5" onSubmit={handleSubmit}>
              <Input type="text" placeholder="Full Name" required value={name} onChange={(e) => setName(e.target.value)} />
              <Input type="email" placeholder="Email Address" required value={email} onChange={(e) => setEmail(e.target.value)} />
              
              <div className="relative">
                <Input
                  type={showPassword ? "text" : "password"}
                  placeholder="Create Password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="pr-10"
                />
                <button
                  type="button"
                  className="absolute right-3 top-3 text-gray-500"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                </button>
              </div>

              <div className="flex items-center text-sm text-gray-600">
                <Check size={16} className="text-green-600 mr-2" />
