// src/context/TeamContext.jsx
import React, { createContext, useContext, useState } from "react";

const TeamContext = createContext();

export const TeamProvider = ({ children }) => {
  const [teams, setTeams] = useState([]);
  const [tasks, setTasks] = useState([]); // global tasks for notifications

  const createTeam = (name, domain) => {
    const newTeam = { id: Date.now(), name, domain, tasks: [] };
    setTeams([...teams, newTeam]);
    return newTeam;
  };

  const addTask = (teamId, task) => {
    setTeams((prevTeams) =>
      prevTeams.map((team) =>
        team.id === teamId
          ? { ...team, tasks: [...team.tasks, task] }
          : team
      )
    );
    setTasks((prevTasks) => [...prevTasks, { ...task, teamId }]);
  };

  const toggleTaskStatus = (teamId, taskId) => {
    setTeams((prevTeams) =>
      prevTeams.map((team) =>
        team.id === teamId
          ? {
              ...team,
              tasks: team.tasks.map((t) =>
                t.id === taskId
                  ? { ...t, status: t.status === "Pending" ? "Completed" : "Pending" }
                  : t
              ),
            }
          : team
      )
    );
    setTasks((prevTasks) =>
      prevTasks.map((t) =>
        t.id === taskId
          ? { ...t, status: t.status === "Pending" ? "Completed" : "Pending" }
          : t
      )
    );
  };

  return (
    <TeamContext.Provider value={{ teams, tasks, createTeam, addTask, toggleTaskStatus }}>
      {children}
    </TeamContext.Provider>
  );
};

export const useTeam = () => useContext(TeamContext);
