import { useState, useEffect } from "react";
import axios from "axios";

export default function Dashboard({ user }) {
  const [stats, setStats] = useState({ teams: 0, tasks: 0 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch overview data
    const fetchStats = async () => {
      try {
        const [teamsRes, tasksRes] = await Promise.all([
          axios.get("http://localhost:5000/api/teams"),
          axios.get("http://localhost:5000/api/tasks") // Adjust to get count
        ]);
        setStats({
          teams: teamsRes.data.length,
          tasks: tasksRes.data.length
        });
      } catch (err) {
        console.error("Dashboard fetch error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  if (loading) return <div className="text-center">Loading dashboard...</div>;

  return (
    <div>
      <h1 className="text-3xl font-bold mb-6">Welcome, {user.name || user.email}!</h1>
      <div className="grid grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold">Your Teams</h2>
          <p className="text-3xl">{stats.teams}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold">Pending Tasks</h2>
          <p className="text-3xl">{stats.tasks}</p>
        </div>
      </div>
    </div>
  );
}
