import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import { User, Mail, Lock } from "lucide-react";

export default function SignupPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const { setAuthData } = useAuth(); // âœ… new helper from context
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      // Call backend API for registration
      const response = await axios.post("http://localhost:5000/api/auth/register", {
        name,
        email,
        password,
      });

      const { token, user } = response.data;

      // âœ… Set auth state directly instead of calling login()
      setAuthData(token, user);

      // Navigate to dashboard
      navigate("/dashboard");
    } catch (err) {
      if (err.response?.status === 400) {
        setError(err.response.data.error || "Email already exists. Please use a different email.");
      } else if (err.response?.status === 401) {
        setError("Signup failed. Please try again.");
      } else {
        setError("Network error. Please try again later.");
      }
      console.error("Signup error:", err);
    } finally {
      setLoading(false);
    }
  };

  // Clear error on input change
  const handleInputChange = () => {
    if (error) setError("");
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-br from-purple-500 via-pink-400 to-blue-400 px-4">
      {/* Animated background blobs */}
      <div className="absolute w-72 h-72 bg-pink-500/30 rounded-full blur-3xl top-10 left-10 animate-pulse"></div>
      <div className="absolute w-96 h-96 bg-purple-500/30 rounded-full blur-3xl bottom-10 right-10 animate-pulse"></div>

      {/* Signup Card */}
      <div className="relative z-10 bg-white/20 backdrop-blur-xl rounded-3xl shadow-2xl w-full max-w-md p-8 border border-white/30 transform transition duration-500 hover:scale-[1.02] hover:shadow-pink-500/30">
        {/* Heading */}
        <div className="mb-6 text-center">
          <h2 className="text-3xl font-extrabold text-white drop-shadow-lg">
            Create Account
          </h2>
          <p className="text-white/80 text-sm mt-1">
            Join us and start your journey ðŸš€
          </p>
        </div>

        {/* Error Message */}
        {error && (
          <div className="mb-4 p-3 bg-red-500/20 border border-red-400/50 text-red-200 text-sm rounded-lg">
            {error}
          </div>
        )}

        <form className="space-y-5" onSubmit={handleSignup}>
          {/* Name */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">Name</label>
            <div className="relative">
              <User className="absolute left-3 top-3.5 text-pink-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => {
                  setName(e.target.value);
                  handleInputChange();
                }}
                className="w-full pl-10 pr-4 py-3 rounded-lg bg-white/80 text-gray-800 border border-transparent focus:border-pink-400 focus:ring-2 focus:ring-pink-500 outline-none transition"
                required
              />
            </div>
          </div>

          {/* Email */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">Email</label>
            <div className="relative">
              <Mail className="absolute left-3 top-3.5 text-purple-400 w-5 h-5" />
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  handleInputChange();
                }}
                className="w-full pl-10 pr-4 py-3 rounded-lg bg-white/80 text-gray-800 border border-transparent focus:border-purple-400 focus:ring-2 focus:ring-purple-500 outline-none transition"
                required
              />
            </div>
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">Password</label>
            <div className="relative">
              <Lock className="absolute left-3 top-3.5 text-blue-400 w-5 h-5" />
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Create a password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);
                  handleInputChange();
                }}
                className="w-full pl-10 pr-12 py-3 rounded-lg bg-white/80 text-gray-800 border border-transparent focus:border-blue-400 focus:ring-2 focus:ring-blue-500 outline-none transition"
                required
                minLength={6}
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-sm text-pink-600 hover:text-pink-700"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
          </div>

          {/* Submit */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full bg-gradient-to-r from-pink-600 via-purple-600 to-blue-600 text-white font-semibold py-3 rounded-lg transition-all shadow-lg hover:shadow-2xl hover:shadow-pink-500/40 ${
              loading
                ? "opacity-50 cursor-not-allowed from-gray-500 via-gray-500 to-gray-500"
                : "hover:from-pink-700 hover:via-purple-700 hover:to-blue-700"
            }`}
          >
            {loading ? "Creating Account..." : "Sign Up"}
          </button>
        </form>

        {/* Divider */}
        <div className="flex items-center my-6">
          <hr className="flex-grow border-white/50" />
          <span className="px-2 text-white/70 text-sm">or</span>
          <hr className="flex-grow border-white/50" />
        </div>

        {/* Social Logins (Placeholder) */}
        <div className="space-y-3">
          <button
            type="button"
            className="w-full flex items-center justify-center gap-3 bg-black/70 text-white font-medium py-3 rounded-lg hover:bg-black/90 transition-all shadow-md"
            onClick={() => {
              alert("Google signup coming soon! Use email/password for now.");
            }}
          >
            <img
              src="/assets/google_icon.svg"
              alt="Google"
              className="h-5 w-5"
            />
            Sign up with Google
          </button>
        </div>

        {/* Footer */}
        <p className="text-center text-white/90 text-sm mt-6">
          Already have an account?{" "}
          <Link to="/login" className="text-yellow-300 hover:underline">
            Log in
          </Link>
        </p>
        <p className="text-center text-white/70 text-xs mt-2">
          By signing up, you agree to our{" "}
          <Link to="/terms" className="text-yellow-300 hover:underline">
            Terms of Use
          </Link>
        </p>
      </div>
    </div>
  );
}
