import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Eye, EyeOff } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";

export default function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include", // ✅ send/receive cookies
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        alert("✅ Login successful!");
        navigate("/dashboard");
      } else {
        alert(data.message || "❌ Invalid login credentials");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-50 via-white to-purple-50 px-4">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-12">
          <img src="/assets/UpToSkills_Logo.svg" alt="Logo" className="h-14 mb-6" />
          <h1 className="text-5xl font-extrabold leading-tight">
            <span className="bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
              AI Learning
            </span>{" "}
            <span className="text-black">Platform</span>
          </h1>
          <p className="text-gray-600 mt-6 text-lg leading-relaxed">
            Unlock the future of education with{" "}
            <span className="font-semibold text-blue-500">AI-powered courses</span> 
            <br />
            designed to accelerate your learning journey.
          </p>
          <div className="mt-12 flex justify-center">
            <img
              src="/assets/login_psge_img.jpg"
              alt="AI Illustration"
              className="max-w-sm rounded-2xl shadow-lg"
            />
          </div>
        </div>

        {/* Right Section (Login Form) */}
        <CardContent className="p-12 bg-white flex flex-col justify-center">
          <div className="w-full max-w-md mx-auto">
            <h2 className="text-3xl font-extrabold text-gray-900 text-center mb-2">
              Welcome Back
            </h2>
            <p className="text-gray-500 text-center mb-8">
              Access your AI learning journey
            </p>

            <form className="space-y-5" onSubmit={handleSubmit}>
              <Input
                type="email"
                name="email"
                placeholder="Enter your email address here"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="rounded-full"
              />
              <div className="relative">
                <Input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  placeholder="Password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="rounded-full pr-10"
                />
                <button
                  type="button"
                  className="absolute right-3 top-3 text-gray-500"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                </button>
              </div>

              <div className="flex justify-between items-center text-sm text-gray-600">
                <label className="flex items-center gap-2 cursor-pointer">
                  <input type="checkbox" className="rounded" />
                  <span>Keep me logged in</span>
                </label>
                <a href="/forgot-password" className="text-blue-600 hover:underline">
                  Forgot Password?
                </a>
              </div>

              <Button
                type="submit"
                disabled={loading}
                className="w-full rounded-full bg-gradient-to-r from-blue-500 to-purple-500 text-white p-3 text-lg shadow-md hover:scale-105 transition-transform"
              >
                {loading ? "Logging in..." : "Log In"}
              </Button>
            </form>

            {/* Divider */}
            <div className="flex items-center my-6">
              <hr className="flex-grow border-gray-300" />
              <span className="px-3 text-gray-400 text-sm">Or</span>
              <hr className="flex-grow border-gray-300" />
            </div>

            {/* Social Logins */}
            <div className="space-y-3">
              <button className="w-full flex items-center justify-center gap-3 rounded-full py-3 bg-white border shadow-sm hover:shadow-md transition">
                <img src="/assets/google_icon.svg" alt="Google" className="h-5 w-5" />
                <span className="font-medium text-gray-700">Sign in with Google</span>
              </button>

              <button className="w-full flex items-center justify-center gap-3 rounded-full py-3 bg-white border shadow-sm hover:shadow-md transition">
                <img src="/assets/apple_icon.svg" alt="Apple" className="h-5 w-5" />
                <span className="font-medium text-gray-700">Sign in with Apple</span>
              </button>
            </div>

            {/* Switch Link */}
            <p className="text-center text-gray-600 mt-6 text-sm">
              Don’t have an account?{" "}
              <a href="/signup" className="text-blue-600 font-semibold hover:underline">
                Sign Up
              </a>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}



