import { useState } from "react";
import { Copy, Plus, Trash2, UserPlus, CheckCircle } from "lucide-react";

export default function Dashboard() {
  const [domains, setDomains] = useState(["Domain 1", "Domain 2"]);
  const [tasks, setTasks] = useState([{ id: 1, title: "Task 1", status: "Completed" }]);
  const [members, setMembers] = useState([
    { id: 1, name: "User 1 (You)", email: "user1@email.com", role: "Lead" },
    { id: 2, name: "Hansuja", email: "hansuja@email.com", role: "Member" },
  ]);
  const [activities, setActivities] = useState(["User 1 created a new team"]);
  const [newActivity, setNewActivity] = useState("");

  const addDomain = () => {
    setDomains([...domains, `Domain ${domains.length + 1}`]);
  };

  const removeDomain = (index) => {
    setDomains(domains.filter((_, i) => i !== index));
  };

  const addTask = () => {
    setTasks([...tasks, { id: tasks.length + 1, title: `Task ${tasks.length + 1}`, status: "Pending" }]);
  };

  const removeMember = (id) => {
    setMembers(members.filter((m) => m.id !== id));
  };

  const addActivity = (e) => {
    e.preventDefault();
    if (!newActivity.trim()) return;
    setActivities([newActivity, ...activities]);
    setNewActivity("");
  };

  return (
    <div className="p-6 space-y-6">
      {/* Team Info */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Team Info Card */}
        <div className="bg-white shadow rounded-xl p-4 space-y-3">
          <h2 className="text-lg font-semibold">‚≠ê Team Info</h2>
          <p className="text-gray-700 font-medium">New Team</p>
          <div className="flex items-center gap-2">
            <input
              type="text"
              value="https://team-join-link.com/example"
              readOnly
              className="flex-1 border rounded-lg px-2 py-1 text-sm"
            />
            <button className="bg-gray-200 px-2 py-1 rounded-lg flex items-center gap-1 hover:bg-gray-300">
              <Copy size={16} /> Copy
            </button>
          </div>
          <button className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700">
            + Add Domain
          </button>
          <button className="w-full bg-gray-400 text-white py-2 rounded-lg hover:bg-gray-500">
            Delete Team
          </button>
        </div>

        {/* Domains */}
        <div className="bg-white shadow rounded-xl p-4 space-y-3">
          <h2 className="text-lg font-semibold">üìë Domains</h2>
          <div className="space-y-2">
            {domains.map((domain, index) => (
              <div
                key={index}
                className="flex justify-between items-center border rounded-lg px-2 py-1"
              >
                <span>{domain}</span>
                <button
                  onClick={() => removeDomain(index)}
                  className="text-red-500 hover:text-red-700"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            ))}
          </div>
          <button
            onClick={addDomain}
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
          >
            + Add Domain
          </button>
          <button className="w-full bg-gray-200 py-2 rounded-lg hover:bg-gray-300">
            Choose Preferred Domains
          </button>
        </div>

        {/* Members */}
        <div className="bg-white shadow rounded-xl p-4 space-y-3">
          <h2 className="text-lg font-semibold">üë• Members</h2>
          <ul className="space-y-2">
            {members.map((member) => (
              <li
                key={member.id}
                className="flex justify-between items-center border rounded-lg px-3 py-2"
              >
                <div>
                  <p className="font-medium">{member.name}</p>
                  <p className="text-sm text-gray-500">{member.email}</p>
                </div>
                {member.role !== "Lead" && (
                  <button
                    onClick={() => removeMember(member.id)}
                    className="text-red-500 hover:text-red-700"
                  >
                    Remove
                  </button>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Tasks & Activity */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Tasks */}
        <div className="bg-white shadow rounded-xl p-4 space-y-3">
          <h2 className="text-lg font-semibold">üìù Tasks</h2>
          <ul className="space-y-2">
            {tasks.map((task) => (
              <li
                key={task.id}
                className="flex justify-between items-center border rounded-lg px-3 py-2"
              >
                <span>
                  {task.title} -{" "}
                  <span
                    className={`${
                      task.status === "Completed" ? "text-green-600" : "text-yellow-600"
                    } font-semibold`}
                  >
                    {task.status}
                  </span>
                </span>
                {task.status === "Completed" && <CheckCircle className="text-green-600" size={18} />}
              </li>
            ))}
          </ul>
          <button
            onClick={addTask}
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
          >
            + Create Task
          </button>
        </div>

        {/* Team Activity */}
        <div className="bg-white shadow rounded-xl p-4 space-y-3">
          <h2 className="text-lg font-semibold">üìå Team Activity</h2>
          <form className="flex gap-2" onSubmit={addActivity}>
            <input
              type="text"
              value={newActivity}
              onChange={(e) => setNewActivity(e.target.value)}
              className="flex-1 border rounded-lg px-2 py-1 text-sm"
              placeholder="Add an activity..."
            />
            <button className="bg-blue-600 text-white px-3 rounded-lg hover:bg-blue-700">
              + Add
            </button>
          </form>
          <ul className="space-y-2 max-h-40 overflow-y-auto">
            {activities.map((act, idx) => (
              <li
                key={idx}
                className="px-3 py-2 border rounded-lg text-sm bg-gray-50"
              >
                {act}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
