import { Menu, Users, Bell, Calendar, User, LogOut, MessageSquare } from "lucide-react";
import { useState } from "react";

export default function Sidebar({ activeSection, setActiveSection }) {
  // Comments state
  const [comments, setComments] = useState([
    { user: "Alice", text: "Great work team!" },
    { user: "Bob", text: "Let's finish the tasks." },
  ]);
  const [newComment, setNewComment] = useState("");

  const handleAddComment = (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;
    setComments([...comments, { user: "You", text: newComment }]);
    setNewComment("");
  };

  return (
    <aside className="w-72 bg-gradient-to-b from-blue-600 via-purple-600 to-pink-600 text-white flex flex-col">
      {/* Logo Section */}
      <div className="p-6 text-2xl font-bold text-center border-b border-white/30">
        AI-Powered Platform
      </div>

      {/* Sidebar Menu */}
      <nav className="flex-1">
        <ul className="space-y-2">
          <li
            className={`flex items-center gap-3 p-3 cursor-pointer hover:bg-white/20 ${activeSection === "menu" ? "bg-white/30" : ""}`}
            onClick={() => setActiveSection("menu")}
          >
            <Menu /> Menu
          </li>
          <li
            className={`flex items-center gap-3 p-3 cursor-pointer hover:bg-white/20 ${activeSection === "teams" ? "bg-white/30" : ""}`}
            onClick={() => setActiveSection("teams")}
          >
            <Users /> Teams
          </li>
          <li
            className={`flex items-center gap-3 p-3 cursor-pointer hover:bg-white/20 ${activeSection === "notifications" ? "bg-white/30" : ""}`}
            onClick={() => setActiveSection("notifications")}
          >
            <Bell /> Notifications
          </li>
          <li
            className={`flex items-center gap-3 p-3 cursor-pointer hover:bg-white/20 ${activeSection === "calendar" ? "bg-white/30" : ""}`}
            onClick={() => setActiveSection("calendar")}
          >
            <Calendar /> Calendar
          </li>
          <li
            className={`flex items-center gap-3 p-3 cursor-pointer hover:bg-white/20 ${activeSection === "profile" ? "bg-white/30" : ""}`}
            onClick={() => setActiveSection("profile")}
          >
            <User /> Profile
          </li>
          <li className="flex items-center gap-3 p-3 hover:bg-white/20 cursor-pointer">
            <LogOut /> Logout
          </li>
        </ul>
      </nav>

      {/* Comments Section */}
      <div className="bg-white/10 p-4 mt-4 rounded-t-xl">
        <h2 className="font-semibold text-lg mb-2 flex items-center gap-2">
          <MessageSquare className="w-5 h-5" /> Comments
        </h2>
        <div className="space-y-2 max-h-40 overflow-y-auto">
          {comments.map((c, i) => (
            <div key={i} className="p-2 bg-white/20 rounded-lg text-sm">
              <span className="font-bold">{c.user}: </span>
              {c.text}
            </div>
          ))}
        </div>
        <form className="flex gap-2 mt-3" onSubmit={handleAddComment}>
          <input
            type="text"
            className="flex-1 border border-gray-300 rounded-lg px-2 py-1 text-black text-sm outline-none"
            placeholder="Add comment..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
          />
          <button className="bg-purple-500 text-white px-3 py-1 rounded-lg hover:bg-purple-600 text-sm">
            Post
          </button>
        </form>
      </div>
    </aside>
  );
}
