import React, { useState } from "react";
import Tasks from "./Tasks";
import Notifications from "./Notifications";

export default function Teams() {
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [domain, setDomain] = useState("");
  const [tasks, setTasks] = useState([]);

  // Handle creating a new team
  const handleCreateTeam = () => {
    const newTeam = { id: Date.now(), name: `Team ${teams.length + 1}`, domain, tasks: [] };
    setTeams([...teams, newTeam]);
    setSelectedTeam(newTeam);
    setDomain(""); // reset input
  };

  // Handle domain selection
  const handleSelectDomain = (teamId, domainName) => {
    const updatedTeams = teams.map((t) =>
      t.id === teamId ? { ...t, domain: domainName } : t
    );
    setTeams(updatedTeams);
    setSelectedTeam(updatedTeams.find((t) => t.id === teamId));
  };

  // Handle adding a task
  const handleAddTask = (task) => {
    if (!selectedTeam) return;
    const updatedTeams = teams.map((t) =>
      t.id === selectedTeam.id ? { ...t, tasks: [...t.tasks, task] } : t
    );
    setTeams(updatedTeams);
    setTasks([...tasks, task]); // update notifications
  };

  return (
    <div className="p-8 space-y-8">
      {/* Team Creation */}
      <section className="bg-white rounded-2xl shadow-lg p-6">
        <h2 className="text-xl font-bold mb-4">Create a New Team</h2>
        <input
          type="text"
          placeholder="Enter Domain/Project Name"
          value={domain}
          onChange={(e) => setDomain(e.target.value)}
          className="border p-2 rounded w-full mb-3"
        />
        <button
          onClick={handleCreateTeam}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg shadow hover:opacity-90"
        >
          Create Team
        </button>
      </section>

      {/* Teams List */}
      <section>
        <h2 className="text-xl font-bold mb-4">Your Teams</h2>
        {teams.length === 0 ? (
          <p className="text-gray-500">No teams created yet.</p>
        ) : (
          <div className="space-y-3">
            {teams.map((team) => (
              <div
                key={team.id}
                className={`p-4 border rounded-lg cursor-pointer ${
                  selectedTeam?.id === team.id ? "bg-blue-50" : "bg-white"
                }`}
                onClick={() => setSelectedTeam(team)}
              >
                <h3 className="font-semibold">{team.name}</h3>
                <p className="text-gray-500">
                  Domain: {team.domain || "Not selected"}
                </p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Task Assignment for Selected Team */}
      {selectedTeam && (
        <Tasks selectedTeam={selectedTeam} onAddTask={handleAddTask} />
      )}

      {/* Notifications */}
      <Notifications tasks={tasks} />
    </div>
  );
}
