import React, { useEffect, useState } from "react";
import axios from "axios";
import Teams from "./Teams";
import Tasks from "./Tasks";

export default function Dashboard() {
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/teams");
      setTeams(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const handleTeamCreated = (team) => {
    setTeams([...teams, team]);
    setNotifications((prev) => [...prev, `Team "${team.name}" created.`]);
  };

  return (
    <div className="p-6 space-y-6">
      <div className="grid md:grid-cols-2 gap-6">
        <Teams onTeamCreated={handleTeamCreated} />
        <div className="bg-white p-6 rounded-2xl shadow-lg space-y-3">
          <h2 className="text-xl font-bold">Notifications</h2>
          {notifications.length === 0 ? (
            <p className="text-gray-500">No notifications yet.</p>
          ) : (
            <ul className="list-disc pl-6 space-y-1">
              {notifications.map((n, idx) => (
                <li key={idx}>{n}</li>
              ))}
            </ul>
          )}
        </div>
      </div>

      <div className="mt-6">
        <h2 className="text-xl font-bold mb-2">Select Team to View Tasks</h2>
        <select
          value={selectedTeam?._id || ""}
          onChange={(e) =>
            setSelectedTeam(teams.find((t) => t._id === e.target.value))
          }
          className="border p-2 rounded"
        >
          <option value="">-- Select Team --</option>
          {teams.map((t) => (
            <option key={t._id} value={t._id}>
              {t.name}
            </option>
          ))}
        </select>
      </div>

      {selectedTeam && <Tasks selectedTeam={selectedTeam} />}
    </div>
  );
}
