import { useState } from "react";
import { Eye, EyeOff } from "lucide-react";
import { Link } from "react-router-dom";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";

export default function SignupPage() {
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const payload = Object.fromEntries(formData.entries());

    const response = await fetch("/api/auth/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();
    alert(data.message || "Success!");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <Card className="w-full max-w-lg shadow-2xl rounded-2xl overflow-hidden">
        <CardContent className="p-10 bg-gray-50 flex flex-col justify-center">
          <h2 className="text-2xl font-bold mb-6 text-center">Join Us Today!</h2>
          <p className="text-gray-500 text-center mb-8">
            Create your account for an enhanced experience
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            <Input
              type="text"
              name="username"
              placeholder="Choose a Username"
              required
              className="p-3 rounded-xl"
            />
            <Input
              type="email"
              name="email"
              placeholder="Email Address"
              required
              className="p-3 rounded-xl"
            />
            <div className="relative">
              <Input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Password"
                required
                className="p-3 rounded-xl pr-10"
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-gray-500"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>

            <Button
              className="w-full rounded-xl bg-blue-600 hover:bg-blue-700 text-white p-3"
              type="submit"
            >
              Sign Up
            </Button>
          </form>

          <div className="my-6 text-center text-gray-400 text-sm">Or</div>
          <div className="flex gap-4 justify-center">
            <button className="border p-3 rounded-full">
              <img src="/assets/google_icon.svg" alt="Google" className="h-6 w-6" />
            </button>
            <button className="border p-3 rounded-full">
              <img src="/assets/apple_icon.svg" alt="Apple" className="h-6 w-6" />
            </button>
          </div>

          <p className="text-center text-gray-600 mt-6">
            Already have an account?
            <Link to="/login" className="ml-2 text-blue-600 font-semibold">
              Log In
            </Link>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
