import React, { useState } from "react";
import axios from "axios";

export default function Teams({ onTeamCreated }) {
  const [teamName, setTeamName] = useState("");
  const [domain, setDomain] = useState("");
  const [members, setMembers] = useState([{ name: "", email: "", role: "Member" }]);

  const addMemberField = () => setMembers([...members, { name: "", email: "", role: "Member" }]);
  const handleMemberChange = (index, key, value) => {
    const newMembers = [...members];
    newMembers[index][key] = value;
    setMembers(newMembers);
  };

  const handleCreateTeam = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/teams", {
        name: teamName,
        domain,
        members,
      });
      onTeamCreated(res.data);
      setTeamName("");
      setDomain("");
      setMembers([{ name: "", email: "", role: "Member" }]);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="p-6 bg-white rounded-2xl shadow-lg space-y-4">
      <h2 className="text-xl font-bold">Create Team</h2>
      <input
        type="text"
        placeholder="Team Name"
        value={teamName}
        onChange={(e) => setTeamName(e.target.value)}
        className="border p-2 rounded w-full"
      />
      <input
        type="text"
        placeholder="Domain / Project Name"
        value={domain}
        onChange={(e) => setDomain(e.target.value)}
        className="border p-2 rounded w-full"
      />
      <div className="space-y-2">
        {members.map((m, idx) => (
          <div key={idx} className="flex gap-2">
            <input
              type="text"
              placeholder="Member Name"
              value={m.name}
              onChange={(e) => handleMemberChange(idx, "name", e.target.value)}
              className="border p-2 rounded flex-1"
            />
            <input
              type="email"
              placeholder="Member Email"
              value={m.email}
              onChange={(e) => handleMemberChange(idx, "email", e.target.value)}
              className="border p-2 rounded flex-1"
            />
          </div>
        ))}
      </div>
      <button onClick={addMemberField} className="text-blue-600 hover:underline">
        + Add Member
      </button>
      <button
        onClick={handleCreateTeam}
        className="bg-blue-600 text-white px-4 py-2 rounded shadow"
      >
        Create Team
      </button>
    </div>
  );
}
