import { useState } from "react";

export default function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-cyan-400 via-purple-500 to-pink-500 px-4">
      <div className="bg-white/10 backdrop-blur-lg border border-white/20 p-8 rounded-2xl shadow-2xl w-full max-w-md text-white">
        <h2 className="text-2xl font-bold mb-6 text-center">Log In</h2>

        <form className="space-y-4">
          {/* Email */}
          <div>
            <label className="block text-sm mb-1">Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              className="w-full px-4 py-3 rounded-md bg-transparent border border-gray-400/40 focus:ring-2 focus:ring-blue-500 outline-none text-white placeholder-gray-300"
              required
            />
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm mb-1">Password</label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Enter password"
                className="w-full px-4 py-3 rounded-md bg-transparent border border-gray-400/40 focus:ring-2 focus:ring-blue-500 outline-none text-white placeholder-gray-300 pr-12"
                required
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-sm text-blue-400 font-medium"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "HIDE" : "SHOW"}
              </button>
            </div>
          </div>

          {/* Submit */}
          <button className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-md transition">
            Log In
          </button>
        </form>

        {/* Divider */}
        <div className="flex items-center my-6">
          <hr className="flex-grow border-gray-500/40" />
          <span className="px-2 text-gray-300 text-sm">or</span>
          <hr className="flex-grow border-gray-500/40" />
        </div>

        {/* Social Logins */}
        <div className="space-y-3">
          <button className="w-full flex items-center justify-center gap-3 bg-white text-gray-800 font-medium py-3 rounded-md hover:bg-gray-100 transition">
            <img src="/assets/google_icon.svg" alt="Google" className="h-5 w-5" />
            Continue with Google
          </button>
          <button className="w-full flex items-center justify-center gap-3 bg-black text-white font-medium py-3 rounded-md hover:bg-gray-900 transition">
            <img src="/assets/apple_icon.svg" alt="Apple" className="h-5 w-5" />
            Continue with Apple
          </button>
        </div>

        {/* Footer */}
        <p className="text-center text-gray-300 text-sm mt-6">
          Don’t have an account?{" "}
          <a href="/signup" className="text-blue-400 hover:underline">
            Sign up
          </a>
        </p>
      </div>
    </div>
  );
}
