import React, { useState } from "react";
import axios from "axios";

export default function Tasks({ selectedTeam }) {
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [assignedTo, setAssignedTo] = useState("");

  if (!selectedTeam) return null;

  const handleAddTask = async () => {
    if (!title || !desc || !assignedTo) return;

    const member = selectedTeam.members.find((m) => m.name === assignedTo);
    const assignedEmail = member?.email;

    try {
      await axios.post("http://localhost:5000/api/tasks", {
        teamId: selectedTeam._id,
        title,
        desc,
        createdBy: "You",
        assignedTo,
        assignedEmail,
      });
      alert(`Task "${title}" assigned to ${assignedTo}`);
      setTitle("");
      setDesc("");
      setAssignedTo("");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="p-6 bg-gray-50 rounded-2xl shadow-lg space-y-3">
      <h2 className="text-xl font-bold mb-4">
        Tasks for {selectedTeam.name} ({selectedTeam.domain})
      </h2>
      <input
        type="text"
        placeholder="Task Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="border p-2 rounded w-full"
      />
      <textarea
        placeholder="Task Description"
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        className="border p-2 rounded w-full"
      />
      <select
        value={assignedTo}
        onChange={(e) => setAssignedTo(e.target.value)}
        className="border p-2 rounded w-full"
      >
        <option value="">Assign To</option>
        {selectedTeam.members.map((m) => (
          <option key={m.email} value={m.name}>
            {m.name}
          </option>
        ))}
      </select>
      <button
        onClick={handleAddTask}
        className="bg-green-600 text-white px-4 py-2 rounded shadow hover:opacity-90"
      >
        Add Task
      </button>
      <div className="mt-4">
        <h3 className="font-semibold">Tasks List</h3>
        {selectedTeam.tasks?.map((t, idx) => (
          <div key={idx} className="border p-2 rounded my-1 bg-white">
            {t.title} - Assigned to: {t.assignedTo} - Status: {t.status}
          </div>
        ))}
      </div>
    </div>
  );
}
