import { useState } from "react";
import { useNavigate, Link } from "react-router-dom"; // For navigation and links
import { useAuth } from "../context/AuthContext"; // From AuthContext (Step 5)
import axios from "axios"; // For API calls
import { Mail, Lock } from "lucide-react"; // Icons

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(""); // For error messages
  const [loading, setLoading] = useState(false); // For submit button

  const { login } = useAuth(); // Get login function from context
  const navigate = useNavigate(); // For navigation after success

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors
    setLoading(true);

    try {
      // Call backend API
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        email,
        password,
      });

      // On success: Extract data and call context login (sets token/user/socket)
      const { token, user } = response.data;
      await login(email, password); // This uses the response internally if needed; adjust if your context login doesn't take params

      // Navigate to dashboard
      navigate("/dashboard");
    } catch (err) {
      // Handle errors
      if (err.response?.status === 401) {
        setError("Invalid email or password. Please try again.");
      } else if (err.response?.status === 400) {
        setError("Login failed. Please check your details.");
      } else {
        setError("Network error. Please try again later.");
      }
      console.error("Login error:", err);
    } finally {
      setLoading(false);
    }
  };

  // Clear error on input change
  const handleInputChange = () => {
    if (error) setError("");
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-br from-purple-600 via-pink-500 to-blue-500 px-4">
      {/* Animated background blobs */}
      <div className="absolute w-72 h-72 bg-pink-500/30 rounded-full blur-3xl top-12 left-12 animate-pulse"></div>
      <div className="absolute w-96 h-96 bg-purple-500/30 rounded-full blur-3xl bottom-12 right-12 animate-pulse"></div>

      {/* Login Card */}
      <div className="relative z-10 bg-white/20 backdrop-blur-xl rounded-3xl shadow-2xl w-full max-w-md p-8 border border-white/30 transform transition duration-500 hover:scale-[1.02] hover:shadow-pink-500/30">
        {/* Heading */}
        <div className="mb-6 text-center">
          <h2 className="text-3xl font-extrabold text-white drop-shadow-lg">
            Welcome Back
          </h2>
          <p className="text-white/80 text-sm mt-1">Log in to continue ðŸš€</p>
        </div>

        {/* Error Message (if any) */}
        {error && (
          <div className="mb-4 p-3 bg-red-500/20 border border-red-400/50 text-red-200 text-sm rounded-lg">
            {error}
          </div>
        )}

        <form className="space-y-5" onSubmit={handleLogin}>
          {/* Email */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">
              Email
            </label>
            <div className="relative">
              <Mail className="absolute left-3 top-3.5 text-purple-400 w-5 h-5" />
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  handleInputChange();
                }}
                className="w-full pl-10 pr-4 py-3 rounded-lg bg-white/90 text-gray-900 border border-transparent focus:border-purple-400 focus:ring-2 focus:ring-purple-500 outline-none transition"
                required
              />
            </div>
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">
              Password
            </label>
            <div className="relative">
              <Lock className="absolute left-3 top-3.5 text-blue-400 w-5 h-5" />
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Enter your password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);
                  handleInputChange();
                }}
                className="w-full pl-10 pr-12 py-3 rounded-lg bg-white/90 text-gray-900 border border-transparent focus:border-blue-400 focus:ring-2 focus:ring-blue-500 outline-none transition"
                required
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-sm text-pink-600 hover:text-pink-700"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
          </div>

          {/* Submit */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 text-white font-semibold py-3 rounded-lg transition-all shadow-lg hover:shadow-2xl hover:shadow-pink-500/40 ${
              loading
                ? "opacity-50 cursor-not-allowed from-gray-500 via-gray-500 to-gray-500"
                : "hover:from-blue-700 hover:via-purple-700 hover:to-pink-700"
            }`}
          >
            {loading ? "Logging In..." : "Log In"}
          </button>
        </form>

        {/* Divider */}
        <div className="flex items-center my-6">
          <hr className="flex-grow border-white/50" />
          <span className="px-2 text-white/70 text-sm">or</span>
          <hr className="flex-grow border-white/50" />
        </div>

        {/* Social Logins (Placeholder - Implement OAuth for full functionality) */}
        <div className="space-y-3">
          <button
            type="button"
            className="w-full flex items-center justify-center gap-3 bg-black/70 text-white font-medium py-3 rounded-lg hover:bg-black/90 transition-all shadow-md"
            onClick={() => {
              // TODO: Implement Google OAuth (e.g., redirect to backend /auth/google)
              alert("Google login coming soon! Use email/password for now.");
            }}
          >
            <img
              src="/assets/google_icon.svg" // Ensure this file exists in /public/assets/
              alt="Google"
              className="h-5 w-5"
            />
            Log in with Google
          </button>
        </div>

        {/* Footer */}
        <p className="text-center text-white/90 text-sm mt-6">
          Donâ€™t have an account?{" "}
          <Link to="/signup" className="text-yellow-300 hover:underline">
            Sign up
          </Link>
        </p>
      </div>
    </div>
  );
}
