// src/context/TeamContext.jsx
import React, { createContext, useContext, useState } from "react";

const TeamContext = createContext();

export const useTeam = () => useContext(TeamContext);

export const TeamProvider = ({ children }) => {
  const [teams, setTeams] = useState([]);
  const [tasks, setTasks] = useState([]);

  const addTeam = (team) => setTeams([...teams, team]);

  const addTask = (teamId, task) => {
    setTeams((prev) =>
      prev.map((t) =>
        t.id === teamId ? { ...t, tasks: [...t.tasks, task] } : t
      )
    );
    setTasks((prev) => [...prev, { ...task, teamId }]);
  };

  const toggleTaskStatus = (teamId, taskId) => {
    setTeams((prev) =>
      prev.map((t) =>
        t.id === teamId
          ? {
              ...t,
              tasks: t.tasks.map((task) =>
                task.id === taskId
                  ? {
                      ...task,
                      status: task.status === "Pending" ? "Completed" : "Pending",
                    }
                  : task
              ),
            }
          : t
      )
    );

    setTasks((prev) =>
      prev.map((task) =>
        task.id === taskId
          ? {
              ...task,
              status: task.status === "Pending" ? "Completed" : "Pending",
            }
          : task
      )
    );
  };

  return (
    <TeamContext.Provider value={{ teams, tasks, addTeam, addTask, toggleTaskStatus }}>
      {children}
    </TeamContext.Provider>
  );
};
