import React, { useState, forwardRef, useImperativeHandle, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const Teams = forwardRef(({ onTeamSelect }, ref) => {
  const { user, token } = useAuth(); // get logged-in user + token

  const [teams, setTeams] = useState([]);
  const [name, setName] = useState("");
  const [domain, setDomain] = useState("");
  const [members, setMembers] = useState(""); // invite emails
  const [loading, setLoading] = useState(false);

  const fetchTeams = async () => {
    try {
      setLoading(true);
      const res = await axios.get("http://localhost:5000/api/teams", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTeams(res.data);
    } catch (err) {
      console.error("Error fetching teams:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTeam = async () => {
    if (!name.trim()) return alert("Team title required!");

    const memberEmails = members.split(",").map((m) => m.trim()).filter(Boolean);

    try {
      const res = await axios.post(
        "http://localhost:5000/api/teams",
        { name, domain, members: memberEmails },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setTeams((prev) => [...prev, res.data]);
      setName(""); setDomain(""); setMembers("");
      if (onTeamSelect) onTeamSelect(res.data);
    } catch (err) {
      console.error("Error creating team:", err);
    }
  };

  useImperativeHandle(ref, () => ({ fetchTeams }));

  useEffect(() => { fetchTeams(); }, []);

  return (
    <div className="p-8 space-y-8">

      {/* Create Allowed Only for Manager */}
      {user?.role === "manager" ? (
        <section className="bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-lg font-bold mb-3">Create Team</h2>

          <input className="border p-2 w-full mb-2" placeholder="Team Name"
            value={name} onChange={(e) => setName(e.target.value)} />
          <input className="border p-2 w-full mb-2" placeholder="Domain"
            value={domain} onChange={(e) => setDomain(e.target.value)} />
          <input className="border p-2 w-full mb-3"
            placeholder="Invite Members (Email, Email, Email)"
            value={members} onChange={(e) => setMembers(e.target.value)} />

          <button onClick={handleCreateTeam}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg">
            ➕ Create Team
          </button>
        </section>
      ) : (
        <p className="px-3 text-gray-600 italic">Only Manager can create teams.</p>
      )}

      {/* Team List */}
      <section>
        <h2 className="font-semibold text-xl mb-3">Teams</h2>

        {loading ? <p>Loading...</p> :
        teams.length === 0 ? <p>No teams yet</p> :

        <div className="space-y-2">
          {teams.map((t) => (
            <div key={t._id}
              onClick={() => onTeamSelect?.(t)}
              className="p-3 border rounded-md cursor-pointer hover:bg-gray-50">
              <b>{t.name}</b> — <span className="text-sm text-gray-600">{t.domain}</span>
              <p className="text-xs text-gray-500">Members: {t.members.join(", ") || "none"}</p>
            </div>
          ))}
        </div>}
      </section>
    </div>
  );
});

export default Teams;
