import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext"; // From Step 5 in previous guide
import SignupPage from "./components/SignupPage";
import LoginPage from "./components/LoginPage";
import DashboardPage from "./components/DashboardPage";
// Placeholder imports for additional pages (create these components next)
import TeamsPage from "./components/Teams";
import TasksPage from "./components/Tasks";
import CalendarPage from "./components/Calendar";
import ActivitiesPage from "./components/Activities";

// Protected Route Component: Wraps routes that require authentication
const ProtectedRoute = ({ children }) => {
  const { user } = useAuth(); // From AuthContext
  return user ? children : <Navigate to="/login" replace />;
};

// Public Route Component: Redirects authenticated users away (e.g., from login/signup)
const PublicRoute = ({ children }) => {
  const { user } = useAuth();
  return user ? <Navigate to="/dashboard" replace /> : children;
};

function AppContent() {
  const { user } = useAuth();

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Optional: Add a global header/navbar here if needed */}
      <Routes>
        {/* Root redirect: To signup if not auth'd, else dashboard */}
        <Route
          path="/"
          element={user ? <Navigate to="/dashboard" /> : <Navigate to="/signup" />}
        />

        {/* Auth Pages (Public) */}
        <Route
          path="/signup"
          element={
            <PublicRoute>
              <SignupPage />
            </PublicRoute>
          }
        />
        <Route
          path="/login"
          element={
            <PublicRoute>
              <LoginPage />
            </PublicRoute>
          }
        />

        {/* Protected Pages */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <DashboardPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/teams"
          element={
            <ProtectedRoute>
              <TeamsPage /> {/* Create this: List/switch teams */}
            </ProtectedRoute>
          }
        />
        <Route
          path="/tasks"
          element={
            <ProtectedRoute>
              <TasksPage /> {/* Create this: Create/assign tasks by domain */}
            </ProtectedRoute>
          }
        />
        <Route
          path="/calendar"
          element={
            <ProtectedRoute>
              <CalendarPage /> {/* Create this: Shared calendar with due dates */}
            </ProtectedRoute>
          }
        />
        <Route
          path="/activities"
          element={
            <ProtectedRoute>
              <ActivitiesPage /> {/* Create this: Realtime activity feed */}
            </ProtectedRoute>
          }
        />

        {/* Catch-all for 404 */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
