import React, { useState } from "react";
import { useTeams } from "../context/TeamContext";
import axios from "axios";

export default function Tasks({ selectedTeam }) {
  const { teams, setTeams, addNotification } = useTeams();
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [assigneeEmail, setAssigneeEmail] = useState("");

  if (!selectedTeam) return null;

  const handleAdd = async () => {
    if (!title || !desc || !assigneeEmail) return;

    const newTask = { title, desc, status: "pending", assigneeEmail };
    const updatedTeams = teams.map((t) =>
      t.id === selectedTeam.id ? { ...t, tasks: [...t.tasks, newTask] } : t
    );
    setTeams(updatedTeams);

    // Add notification
    addNotification(`Task "${title}" assigned to ${assigneeEmail}`);

    // Send email via backend
    try {
      await axios.post("http://localhost:5000/api/send-email", {
        to: assigneeEmail,
        subject: `New Task Assigned: ${title}`,
        text: `You have been assigned a new task in team "${selectedTeam.name}":\n\n${desc}`,
      });
    } catch (err) {
      console.error("Email sending failed", err);
    }

    setTitle("");
    setDesc("");
    setAssigneeEmail("");
  };

  return (
    <div className="p-6 bg-gray-50 rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">
        Tasks for {selectedTeam.name} ({selectedTeam.domain})
      </h2>

      <div className="mb-6 space-y-2">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <textarea
          placeholder="Task Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <input
          type="email"
          placeholder="Assignee Email"
          value={assigneeEmail}
          onChange={(e) => setAssigneeEmail(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <button
          onClick={handleAdd}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:opacity-90"
        >
          Add Task
        </button>
      </div>

      <div className="space-y-3">
        {selectedTeam.tasks.length === 0 ? (
          <p className="text-gray-500">No tasks assigned yet.</p>
        ) : (
          selectedTeam.tasks.map((t, i) => (
            <div key={i} className="p-4 border rounded-lg shadow bg-white">
              <h3 className="font-semibold">{t.title}</h3>
              <p className="text-gray-600 text-sm">{t.desc}</p>
              <p className="text-xs mt-1">
                <span className="font-medium">Assignee:</span> {t.assigneeEmail}
              </p>
              <p className="text-xs mt-1">
                <span className="font-medium">Status:</span>{" "}
                <span className={t.status === "pending" ? "text-red-500" : "text-green-600"}>
                  {t.status}
                </span>
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
