import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Eye, EyeOff, Check } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";

export default function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  // Prefill from localStorage (after signup)
  useEffect(() => {
    const savedEmail = localStorage.getItem("signupEmail");
    const savedPassword = localStorage.getItem("signupPassword");
    if (savedEmail && savedPassword) {
      setEmail(savedEmail);
      setPassword(savedPassword);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        alert("✅ Login successful!");
        // clear saved signup credentials after login
        localStorage.removeItem("signupEmail");
        localStorage.removeItem("signupPassword");
        navigate("/dashboard"); 
      } else {
        alert(data.message || "❌ Invalid login credentials");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-10">
          <img src="/assets/UpToSkills_Logo.svg" alt="Logo" className="h-12 mb-6" />
          <h1 className="text-4xl font-extrabold">
            <span className="text-blue-500">AI Learning</span>{" "}
            <span className="text-black">Platform</span>
          </h1>
          <p className="text-gray-600 mt-4 text-lg">
            Unlock the future of education with AI-powered courses
            <br />
            designed to accelerate your learning journey
          </p>
          <div className="mt-8 flex justify-center">
            <img src="/assets/login_psge_img.jpg" alt="AI Illustration" className="max-w-sm" />
          </div>
        </div>

        {/* Right Section */}
        <CardContent className="p-10 bg-gray-50 flex flex-col justify-center">
          <h2 className="text-2xl font-bold mb-2">Welcome Back</h2>
          <p className="text-gray-500 mb-6">Access your AI learning journey</p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            <Input 
              type="email" 
              name="email" 
              placeholder="Email Address" 
              required 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
            <div className="relative">
              <Input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Password"
                required
                className="pr-10"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-gray-500"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>

            <div className="flex justify-between items-center text-sm text-gray-600">
              <label className="flex items-center gap-2">
                <Check size={16} className="text-green-600" /> Keep me logged in
              </label>
              <a href="/forgot-password" className="text-blue-600">
                Forgot Password?
              </a>
            </div>

            <Button
              type="submit"
              disabled={loading}
              className="w-full rounded-xl bg-blue-600 hover:bg-blue-700 text-white p-3"
            >
              {loading ? "Logging in..." : "Log In"}
            </Button>
          </form>

          {/* Divider */}
          <div className="my-6 text-center text-gray-400 text-sm">Or</div>

          {/* Social Logins */}
          <div className="space-y-3">
            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src="/assets/google_icon.svg" alt="Google" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Sign in with Google</span>
            </button>

            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src="/assets/apple_icon.svg" alt="Apple" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Sign in with Apple</span>
            </button>
          </div>

          {/* Switch Link */}
          <p className="text-center text-gray-600 mt-6">
            Don’t have an account?{" "}
            <a href="/signup" className="text-blue-600 font-semibold">
              Sign Up
            </a>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
