// src/components/DashboardPage.jsx
import React from "react";
import { useTeam } from "../context/TeamContext";

export default function DashboardPage() {
  const { teams, tasks } = useTeam();

  return (
    <div className="p-6 space-y-6">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

      {/* Teams */}
      <section className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {teams.length === 0 ? (
          <p className="text-gray-500">No teams created yet.</p>
        ) : (
          teams.map((team) => (
            <div key={team.id} className="bg-white shadow rounded-xl p-4">
              <h2 className="text-lg font-semibold">{team.name}</h2>
              <p className="text-gray-500">Domain: {team.domain}</p>
              <h3 className="font-semibold mt-2">Tasks:</h3>
              {team.tasks.length === 0 ? (
                <p className="text-gray-500">No tasks assigned</p>
              ) : (
                <ul className="space-y-1">
                  {team.tasks.map((task) => (
                    <li
                      key={task.id}
                      className={`${
                        task.status === "Completed" ? "text-green-600" : "text-yellow-600"
                      } font-semibold`}
                    >
                      {task.title} - {task.status}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          ))
        )}
      </section>

      {/* Notifications */}
      <section>
        <h2 className="text-xl font-bold mb-2">Notifications</h2>
        {tasks.length === 0 ? (
          <p className="text-gray-500">No notifications yet.</p>
        ) : (
          <ul className="space-y-2">
            {tasks.map((task) => {
              const team = teams.find((t) => t.id === task.teamId);
              return (
                <li
                  key={task.id}
                  className={`border px-3 py-2 rounded bg-gray-50 ${
                    task.status === "Completed" ? "line-through text-green-600" : ""
                  }`}
                >
                  Task "{task.title}" in team "{team?.name}" - {task.status}
                </li>
              );
            })}
          </ul>
        )}
      </section>
    </div>
  );
}
