import { useState } from "react";
import { Eye, EyeOff } from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const payload = Object.fromEntries(formData.entries());

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) {
        localStorage.setItem("token", data.token);
        alert("✅ Login successful!");
        navigate("/dashboard");
      } else {
        alert(data.message || "Login failed!");
      }
    } catch (err) {
      console.error(err);
      alert("Server error!");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <div className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-10">
          <img src="/assets/UpToSkills_Logo.png" alt="Logo" className="h-12 mb-6" />
          <h1 className="text-5xl font-extrabold leading-tight">
            <span className="bg-gradient-to-r from-blue-500 to-purple-500 bg-clip-text text-transparent">
              Welcome Back
            </span>
          </h1>
          <p className="text-gray-600 mt-6 text-lg">
            Log in to continue your AI-powered learning journey
          </p>
          <div className="mt-10 flex justify-center">
            <img src="/assets/login_page_img.jpg" alt="Illustration" className="max-w-sm" />
          </div>
        </div>

        {/* Right Section */}
        <div className="p-10 bg-white flex flex-col justify-center">
          <h2 className="text-xl font-bold mb-1">Welcome Back!</h2>
          <p className="text-gray-500 mb-6 text-sm">
            Please log in to your account.
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              required
              className="w-full border rounded-lg p-3"
            />

            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Enter your password"
                required
                className="w-full border rounded-lg p-3 pr-10"
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-gray-500"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>

            <button className="w-full rounded-xl bg-purple-500 hover:bg-purple-600 text-white p-3 text-lg">
              Log In
            </button>
          </form>

          {/* Divider */}
          <div className="flex items-center my-6">
            <hr className="flex-grow border-gray-300" />
            <span className="px-3 text-gray-400 text-sm">Or</span>
            <hr className="flex-grow border-gray-300" />
          </div>

          {/* Social Logins */}
          <div className="space-y-3 mt-4">
            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src="/assets/google_icon.png" alt="Google" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Log in with Google</span>
            </button>
            <button className="w-full flex items-center justify-center gap-3 border rounded-xl py-3 hover:bg-gray-100 transition">
              <img src="/assets/apple_icon.png" alt="Apple" className="h-5 w-5" />
              <span className="font-medium text-gray-700">Log in with Apple</span>
            </button>
          </div>

          {/* Switch Link */}
          <p className="text-center text-gray-600 mt-6 text-sm">
            Don’t have an account?{" "}
            <a href="/" className="text-red-500 font-semibold">
              Sign Up
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}






