// src/components/Teams.jsx
import React, { useState, useRef } from "react";
import { useTeam } from "../context/TeamContext";

export default function Teams() {
  const { teams, addTeam, addTask, toggleTaskStatus } = useTeam();
  const [domain, setDomain] = useState("");
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [taskTitle, setTaskTitle] = useState("");
  const taskRefs = useRef({});

  const handleCreateTeam = () => {
    if (!domain) return;
    const newTeam = { id: Date.now(), name: `Team ${teams.length + 1}`, domain, tasks: [] };
    addTeam(newTeam);
    setSelectedTeam(newTeam);
    setDomain("");
  };

  const handleAddTask = () => {
    if (!selectedTeam || !taskTitle) return;
    const task = { id: Date.now(), title: taskTitle, status: "Pending" };
    addTask(selectedTeam.id, task);
    setTaskTitle("");
  };

  const scrollToTask = (taskId) => {
    const taskElement = taskRefs.current[taskId];
    if (taskElement) {
      taskElement.scrollIntoView({ behavior: "smooth", block: "center" });
      taskElement.classList.add("ring-4", "ring-blue-300");
      setTimeout(() => {
        taskElement.classList.remove("ring-4", "ring-blue-300");
      }, 2000);
    }
  };

  return (
    <div className="p-8 space-y-8">
      {/* Team Creation */}
      <section className="bg-white rounded-2xl shadow-lg p-6">
        <h2 className="text-xl font-bold mb-4">Create a New Team</h2>
        <input
          type="text"
          placeholder="Enter Domain/Project Name"
          value={domain}
          onChange={(e) => setDomain(e.target.value)}
          className="border p-2 rounded w-full mb-3"
        />
        <button
          onClick={handleCreateTeam}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg shadow hover:opacity-90"
        >
          Create Team
        </button>
      </section>

      {/* Teams List */}
      <section>
        <h2 className="text-xl font-bold mb-4">Your Teams</h2>
        {teams.length === 0 ? (
          <p className="text-gray-500">No teams created yet.</p>
        ) : (
          <div className="space-y-3">
            {teams.map((team) => (
              <div
                key={team.id}
                className={`p-4 border rounded-lg cursor-pointer ${
                  selectedTeam?.id === team.id ? "bg-blue-50" : "bg-white"
                }`}
                onClick={() => setSelectedTeam(team)}
              >
                <h3 className="font-semibold">{team.name}</h3>
                <p className="text-gray-500">Domain: {team.domain}</p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Tasks for Selected Team */}
      {selectedTeam && (
        <section className="bg-white rounded-2xl shadow-lg p-6 mt-4">
          <h2 className="text-xl font-bold mb-2">Tasks for {selectedTeam.name}</h2>
          <input
            type="text"
            placeholder="Task Title"
            value={taskTitle}
            onChange={(e) => setTaskTitle(e.target.value)}
            className="border p-2 rounded w-full mb-2"
          />
          <button
            onClick={handleAddTask}
            className="bg-green-600 text-white px-6 py-2 rounded-lg shadow hover:opacity-90"
          >
            Add Task
          </button>

          <ul className="mt-4 space-y-1">
            {selectedTeam.tasks.map((task) => (
              <li
                key={task.id}
                ref={(el) => (taskRefs.current[task.id] = el)}
                className={`border px-2 py-1 rounded flex justify-between items-center ${
                  task.status === "Completed"
                    ? "line-through text-green-600"
                    : "text-yellow-600"
                }`}
              >
                <span>{task.title} - {task.status}</span>
                <button
                  onClick={() => toggleTaskStatus(selectedTeam.id, task.id)}
                  className="bg-gray-200 px-2 py-1 rounded hover:bg-gray-300 text-sm"
                >
                  {task.status === "Pending" ? "Mark Completed" : "Mark Pending"}
                </button>
              </li>
            ))}
          </ul>
        </section>
      )}
    </div>
  );
}
