import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Mail, Lock } from "lucide-react";

export default function LoginPage() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();

    // âœ… Mock authentication
    localStorage.setItem("accessToken", "dummy-token"); // set token

    // âœ… Redirect to dashboard
    navigate("/dashboard");
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-br from-purple-600 via-pink-500 to-blue-500 px-4">
      {/* Animated background blobs */}
      <div className="absolute w-72 h-72 bg-pink-500/30 rounded-full blur-3xl top-12 left-12 animate-pulse"></div>
      <div className="absolute w-96 h-96 bg-purple-500/30 rounded-full blur-3xl bottom-12 right-12 animate-pulse"></div>

      {/* Login Card */}
      <div className="relative z-10 bg-white/20 backdrop-blur-xl rounded-3xl shadow-2xl w-full max-w-md p-8 border border-white/30 transform transition duration-500 hover:scale-[1.02] hover:shadow-pink-500/30">
        {/* Heading */}
        <div className="mb-6 text-center">
          <h2 className="text-3xl font-extrabold text-white drop-shadow-lg">
            Welcome Back
          </h2>
          <p className="text-white/80 text-sm mt-1">Log in to continue ðŸš€</p>
        </div>

        <form className="space-y-5" onSubmit={handleLogin}>
          {/* Email */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">Email</label>
            <div className="relative">
              <Mail className="absolute left-3 top-3.5 text-purple-400 w-5 h-5" />
              <input
                type="email"
                placeholder="Enter your email"
                className="w-full pl-10 pr-4 py-3 rounded-lg bg-white/90 text-gray-900 border border-transparent focus:border-purple-400 focus:ring-2 focus:ring-purple-500 outline-none transition"
                required
              />
            </div>
          </div>

          {/* Password */}
          <div>
            <label className="block text-sm font-medium text-white mb-1">Password</label>
            <div className="relative">
              <Lock className="absolute left-3 top-3.5 text-blue-400 w-5 h-5" />
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Enter your password"
                className="w-full pl-10 pr-12 py-3 rounded-lg bg-white/90 text-gray-900 border border-transparent focus:border-blue-400 focus:ring-2 focus:ring-blue-500 outline-none transition"
                required
              />
              <button
                type="button"
                className="absolute right-3 top-3 text-sm text-pink-600 hover:text-pink-700"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
          </div>

          {/* Forgot Password */}
          <div className="text-right">
            <a href="#" className="text-sm text-yellow-300 hover:underline">
              Forgot password?
            </a>
          </div>

          {/* Submit */}
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 hover:from-blue-700 hover:via-purple-700 hover:to-pink-700 text-white font-semibold py-3 rounded-lg transition-all shadow-lg hover:shadow-2xl hover:shadow-pink-500/40"
          >
            Log In
          </button>
        </form>
      </div>
    </div>
  );
}
