import { useState } from "react";
import axios from "axios";

export default function IdeaGeneration() {
  const [domain, setDomain] = useState("");
  const [idea, setIdea] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleGenerateIdea = async () => {
    if (!domain.trim()) return;
    setLoading(true);
    setError("");
    setIdea("");

    try {
      const res = await axios.post("http://localhost:5000/api/idea", { domain });
      setIdea(res.data.idea);
    } catch (err) {
      console.error(err);
      setError("Failed to generate idea. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-white rounded-2xl shadow-lg max-w-2xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Generate Project Ideas</h2>

      <input
        type="text"
        placeholder="Enter domain or project area"
        value={domain}
        onChange={(e) => setDomain(e.target.value)}
        className="w-full p-3 border rounded-lg mb-4 focus:ring-2 focus:ring-blue-500 outline-none"
      />

      <button
        onClick={handleGenerateIdea}
        disabled={loading}
        className={`w-full bg-blue-600 text-white py-3 rounded-lg font-semibold ${
          loading ? "opacity-50 cursor-not-allowed" : "hover:bg-blue-700"
        }`}
      >
        {loading ? "Generating..." : "Generate Idea"}
      </button>

      {error && <p className="text-red-500 mt-4">{error}</p>}

      {idea && (
        <div className="mt-6 p-4 bg-gray-50 border rounded-lg">
          <h3 className="font-semibold mb-2">Suggested Idea:</h3>
          <p>{idea}</p>
        </div>
      )}
    </div>
  );
}
