// src/context/TeamContext.jsx
import React, { createContext, useState, useContext } from "react";

const TeamContext = createContext();

export const TeamProvider = ({ children }) => {
  const [teams, setTeams] = useState([]);

  const addTeam = (team) => {
    setTeams((prev) => [...prev, team]);
  };

  const addTaskToTeam = (teamId, task) => {
    setTeams((prev) =>
      prev.map((t) => (t.id === teamId ? { ...t, tasks: [...t.tasks, task] } : t))
    );
  };

  return (
    <TeamContext.Provider value={{ teams, setTeams, addTeam, addTaskToTeam }}>
      {children}
    </TeamContext.Provider>
  );
};

export const useTeams = () => useContext(TeamContext);
