import { useState } from "react";
import { Eye, EyeOff } from "lucide-react";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Card, CardContent } from "./ui/card";

export default function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [isForgotPassword, setIsForgotPassword] = useState(false);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const payload = Object.fromEntries(formData.entries());

    const endpoint = isForgotPassword
      ? "/api/auth/forgot-password"
      : "/api/auth/login";

    const response = await fetch(endpoint, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });

    const data = await response.json();
    alert(data.message || "Success!");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-white px-4">
      <Card className="w-full max-w-6xl grid md:grid-cols-2 shadow-2xl rounded-2xl overflow-hidden">
        {/* Left Section */}
        <div className="flex flex-col justify-center bg-white p-10">
          <img src="/assets/UpToSkills_Logo.svg" alt="Logo" className="h-12 mb-6" />
          <h1 className="text-4xl font-extrabold">
            <span className="text-blue-500">AI Learning</span>{" "}
            <span className="text-black">Platform</span>
          </h1>
          <p className="text-gray-600 mt-4 text-lg">
            Unlock the future of education with AI-powered courses
            <br />
            designed to accelerate your learning journey
          </p>
          <div className="mt-8 flex justify-center">
            <img src="/assets/login_psge_img.jpg" alt="AI Illustration" className="max-w-sm" />
          </div>
        </div>

        {/* Right Section */}
        <CardContent className="p-10 bg-gray-50 flex flex-col justify-center">
          <h2 className="text-2xl font-bold mb-6 text-center">
            {isForgotPassword ? "Forgot Password" : "Welcome Back"}
          </h2>
          <p className="text-gray-500 text-center mb-8">
            {isForgotPassword
              ? "Enter your email to reset your password"
              : "Access your AI learning journey"}
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            {isForgotPassword ? (
              <Input type="email" name="email" placeholder="Email Address" required className="p-3 rounded-xl" />
            ) : (
              <>
                <Input type="email" name="email" placeholder="Email Address" required className="p-3 rounded-xl" />
                <div className="relative">
                  <Input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    placeholder="Password"
                    required
                    className="p-3 rounded-xl pr-10"
                  />
                  <button
                    type="button"
                    className="absolute right-3 top-3 text-gray-500"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </>
            )}

            {!isForgotPassword && (
              <div className="flex justify-between items-center text-sm text-gray-600">
                <label className="flex items-center gap-2">
                  <input type="checkbox" /> Keep me logged in
                </label>
                <button
                  type="button"
                  onClick={() => setIsForgotPassword(true)}
                  className="text-blue-600"
                >
                  Forgot Password?
                </button>
              </div>
            )}

            <Button className="w-full rounded-xl bg-blue-600 hover:bg-blue-700 text-white p-3" type="submit">
              {isForgotPassword ? "Send Reset Link" : "Log In"}
            </Button>
          </form>

          {!isForgotPassword && (
            <>
              <div className="my-6 text-center text-gray-400 text-sm">Or</div>
              <div className="flex gap-4 justify-center">
                <button className="border p-3 rounded-full">
                  <img src="/assets/google_icon.svg" alt="Google" className="h-6 w-6" />
                </button>
                <button className="border p-3 rounded-full">
                  <img src="/assets/apple_icon.svg" alt="Apple" className="h-6 w-6" />
                </button>
              </div>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
}
