import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext"; // From Step 5 in previous guide
import SignupPage from "./components/SignupPage";
import LoginPage from "./components/LoginPage";
import DashboardPage from "./components/DashboardPage";

// Protected Route Component: Wraps routes that require authentication
const ProtectedRoute = ({ children }) => {
  const { user } = useAuth(); // From AuthContext
  return user ? children : <Navigate to="/login" replace />;
};

// Public Route Component: Redirects authenticated users away (e.g., from login/signup)
const PublicRoute = ({ children }) => {
  const { user } = useAuth();
  return user ? <Navigate to="/dashboard" replace /> : children;
};

// Simple 404 Component
const NotFound = () => (
  <div className="flex items-center justify-center min-h-screen text-center">
    <div>
      <h1 className="text-4xl font-bold text-gray-600 mb-4">404 - Page Not Found</h1>
      <p className="text-gray-500 mb-4">The page you're looking for doesn't exist.</p>
      <a href="/" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
        Go Home
      </a>
    </div>
  </div>
);

function AppContent() {
  const { user } = useAuth();

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Optional: Add a global header/navbar here if needed (but DashboardPage has its own) */}
      <Routes>
        {/* Root redirect: To signup if not auth'd, else dashboard */}
        <Route
          path="/"
          element={user ? <Navigate to="/dashboard" /> : <Navigate to="/signup" />}
        />

        {/* Auth Pages (Public) */}
        <Route
          path="/signup"
          element={
            <PublicRoute>
              <SignupPage />
            </PublicRoute>
          }
        />
        <Route
          path="/login"
          element={
            <PublicRoute>
              <LoginPage />
            </PublicRoute>
          }
        />

        {/* Main Protected Dashboard (handles all sections internally) */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <DashboardPage />
            </ProtectedRoute>
          }
        />

        {/* Catch-all for 404 */}
        <Route path="*" element={<NotFound />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
