import { useState } from "react";
import Sidebar from "./Sidebar";
import Navbar from "./Navbar";

export default function DashboardPage() {
  // Chat state
  const [messages, setMessages] = useState([
    { user: "Alice", text: "Hello team!" },
    { user: "Bob", text: "Hi Alice!" },
  ]);
  const [newMessage, setNewMessage] = useState("");

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    setMessages([...messages, { user: "You", text: newMessage }]);
    setNewMessage("");
  };

  // Comments state
  const [comments, setComments] = useState([
    { user: "Alice", text: "Great work team!" },
    { user: "Bob", text: "Let's finish the tasks." },
  ]);
  const [newComment, setNewComment] = useState("");

  const handleAddComment = (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;
    setComments([...comments, { user: "You", text: newComment }]);
    setNewComment("");
  };

  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar />

        {/* Content Area */}
        <main className="flex-1 p-6 overflow-y-auto">
          {/* Chat Section */}
          <div className="bg-white rounded-xl shadow p-4 flex flex-col h-[70vh]">
            <h2 className="font-semibold text-lg mb-4">Team Chat</h2>
            <div className="flex-1 overflow-y-auto mb-4 space-y-2">
              {messages.map((msg, index) => (
                <div
                  key={index}
                  className={`flex ${
                    msg.user === "You" ? "justify-end" : "justify-start"
                  }`}
                >
                  <div
                    className={`p-2 rounded-lg max-w-[70%] ${
                      msg.user === "You"
                        ? "bg-blue-100 text-right"
                        : "bg-gray-100"
                    }`}
                  >
                    <span className="font-bold">{msg.user}: </span>
                    {msg.text}
                  </div>
                </div>
              ))}
            </div>
            <form className="flex gap-2" onSubmit={handleSendMessage}>
              <input
                type="text"
                className="flex-1 border border-gray-300 rounded-lg px-3 py-2 outline-none"
                placeholder="Type a message..."
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition">
                Send
              </button>
            </form>
          </div>

          {/* Comments Section */}
          <div className="bg-white rounded-xl shadow p-4 mt-6">
            <h2 className="font-semibold text-lg mb-2">Comments</h2>
            <div className="space-y-2">
              {comments.map((c, i) => (
                <div key={i} className="p-2 bg-gray-100 rounded-lg">
                  <span className="font-bold">{c.user}: </span>
                  {c.text}
                </div>
              ))}
            </div>
            <form className="flex gap-2 mt-3" onSubmit={handleAddComment}>
              <input
                type="text"
                className="flex-1 border border-gray-300 rounded-lg px-3 py-2 outline-none"
                placeholder="Add a comment..."
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
              />
              <button className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition">
                Post
              </button>
            </form>
          </div>
        </main>
      </div>
    </div>
  );
}
