import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Tasks({ selectedTeam }) {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [assignedTo, setAssignedTo] = useState(""); // comma separated emails

  // Fetch tasks for selected team
  const fetchTasks = async () => {
    if (!selectedTeam) return;
    try {
      const res = await axios.get("http://localhost:5000/api/tasks");
      const teamTasks = res.data.filter((t) => t.teamId === selectedTeam._id);
      setTasks(teamTasks);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, [selectedTeam]);

  const handleAddTask = async () => {
    if (!title || !desc) return;
    try {
      const emails = assignedTo.split(",").map((e) => e.trim());
      const res = await axios.post("http://localhost:5000/api/tasks", {
        title,
        desc,
        teamId: selectedTeam._id,
        assignedTo: emails,
        createdBy: "lead@example.com", // Replace with logged-in user's email
      });
      setTasks([...tasks, res.data]);
      setTitle("");
      setDesc("");
      setAssignedTo("");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-6 bg-gray-50 rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">
        Tasks for {selectedTeam.name} ({selectedTeam.domain || "No domain"})
      </h2>

      <div className="mb-6 space-y-2">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <textarea
          placeholder="Task Description"
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <input
          type="text"
          placeholder="Assign to emails (comma separated)"
          value={assignedTo}
          onChange={(e) => setAssignedTo(e.target.value)}
          className="border p-2 rounded w-full"
        />
        <button
          onClick={handleAddTask}
          className="bg-green-600 text-white px-4 py-2 rounded shadow hover:opacity-90"
        >
          Add Task
        </button>
      </div>

      <div className="space-y-3">
        {tasks.length === 0 ? (
          <p className="text-gray-500">No tasks assigned yet.</p>
        ) : (
          tasks.map((task) => (
            <div key={task._id} className="p-4 border rounded-lg shadow bg-white">
              <h3 className="font-semibold">{task.title}</h3>
              <p className="text-gray-600 text-sm">{task.desc}</p>
              <p className="text-xs mt-1">
                <span className="font-medium">Status:</span>{" "}
                <span className={task.status === "completed" ? "text-green-600" : "text-red-500"}>
                  {task.status}
                </span>
              </p>
              <p className="text-xs mt-1">
                <span className="font-medium">Assigned To:</span> {task.assignedTo.join(", ")}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
