import Sidebar from "./Sidebar";
import Navbar from "./Navbar";
import welcomeImg from "../assets/welcome.png"; // replace with your image

export default function DashboardPage() {
  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <Navbar />

        {/* Content Area */}
        <main className="flex-1 p-6 overflow-y-auto">
          {/* Welcome Section */}
          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center text-center">
            <img
              src={welcomeImg}
              alt="Welcome"
              className="w-64 h-64 object-contain mb-6"
            />
            <h2 className="text-2xl font-bold text-gray-800 mb-3">
              Welcome to the AI-Powered Ideation & Project Management Platform
            </h2>
            <p className="text-gray-600 max-w-2xl">
              This platform helps your team brainstorm innovative ideas,
              collaborate seamlessly, and manage projects efficiently with the
              power of AI. Use the sidebar to explore teams, notifications,
              calendar, and profile. Stay productive and connected!
            </p>
          </div>
        </main>
      </div>
    </div>
  );
}
